                        author
                    }
                })
                this.setState({posts: res.data})
            })
            .catch(err => console.log(err))
    }

    render () {
        const posts = this.state.posts.map(post => {
            return <Post 
            title={post.title}
            key={post.id}
            />
        })
        return (
            <div>
                <section className="Posts">
                    {posts}
                </section>
                <section>
                    <FullPost />
                </section>
                <section>
                    <NewPost />
                </section>
            </div>
        );
    }
}

export default Blog;
